package au.accolite.restaurant;

// TODO: Auto-generated Javadoc
/**
 * The Class Order.
 */
public class Order {
	
	/** The table no. */
	private int tableNo;
	
	/** The amount. */
	private double amount;
	
	/**
	 * Gets the table no.
	 *
	 * @return the table no
	 */
	public int getTableNo() {
		return tableNo;
	}
	
	/**
	 * Sets the table no.
	 *
	 * @param tableNo the new table no
	 */
	public void setTableNo(int tableNo) {
		this.tableNo = tableNo;
	}
	
	/**
	 * Gets the amount.
	 *
	 * @return the amount
	 */
	public double getAmount() {
		return amount;
	}
	
	/**
	 * Sets the amount.
	 *
	 * @param amount the new amount
	 */
	public void setAmount(double amount) {
		this.amount = amount;
	}

	
}
